import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  loginForm: FormGroup;

  constructor(
    private service: AuthService,
    private fb: FormBuilder,
    private router: Router
  ) { }

  ngOnInit() {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required]],

  })
  }

  login() {
    console.log(this.loginForm.value);
    this.service.login(this.loginForm.value).subscribe((response) => {
      console.log(response);
      if (response.jwtToken) {

        const jwtToken = response.jwtToken;
        localStorage.setItem('JWT', jwtToken);
        this.router.navigate(['dashboard']);
      }
    })
  }

}


    <div class="form-bg">

        <form [formGroup]="loginForm" class="form">
            <div class="box">

                <div style="display: flex; margin: 0 auto;">
                    <h4 class="admin">LogIn</h4>
                </div>
                <div>
                    <input type="email" name="adminName" id="email" placeholder="Enter email" formControlName="email" required/>
                </div>
                <div>
                    <input type="password" name="password" id="password" placeholder="Enter password" formControlName="password" required/>
                </div>
                <div>
                    <button class="SignUp" type="button" routerLink="/signup">Sign-Up ?</button>
                </div>

                <button class="login-button" type="submit" (click)="login()" [disabled]="loginForm.invalid">Log In</button>
            </div>
        </form>
    </div>
